import React, { useEffect, useState } from "react";

import axios from "axios";

const possibleWaypoints = [
  { name: "Espoo", rating: 5 },
  { name: "Helsinki", rating: 4 },
  { name: "Vantaa", rating: 3 },
];

const App = () => {
  const [waypoints, setWaypoints] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/api/path/get").then((res) => {
      setWaypoints(res.data);
    });
  }, []);

  const generate = () => {
    const randomWaypoint = () => {
      const randIndex = Math.floor(Math.random() * possibleWaypoints.length);
      const randomizedWaypoint = possibleWaypoints[randIndex];
      return randomizedWaypoint;
    };

    const randomPath = {
      name: "TEST",
      rating: 5,
    };

    const randomWaypoints = Array.from({ length: 2 }, randomWaypoint);

    axios
      .post("http://localhost:3001/api/paths/insert", randomPath)
      .then((res) => {
        const { stuff } = res.data[0];
        console.log("after path insertion", stuff);
        randomWaypoints.forEach((waypoint) =>
          axios
            .post("http://localhost:3001/api/waypoints/insert", {
              ...waypoint,
              pathID: res.data[0].LAST_INSERT_ID(),
            })
            .then(() => {})
        );
      });
    setWaypoints(randomWaypoints);

    /*
    axios
      .post("http://localhost:3001/api/insert", randomizedWaypoint)
      .then(() => {});
    setWaypoints([...waypoints, randomizedWaypoint]);
    */
  };
  return (
    <div>
      <h1>PathBuilder</h1>
      <button onClick={generate}>Generate waypoint!</button>
      {waypoints.map((waypoint) => (
        <p key={waypoint.ID}>
          {waypoint.name} of rating {waypoint.rating}
        </p>
      ))}
    </div>
  );
};

export default App;
